import styled from "@emotion/styled";

//types
export const H1 = styled.h1({
  color: "purple",
  fontSize: 100,
  marginTop: "100px",
  textAlign: "center",
});

export const Bio = styled.h1({
  color: "darkmagenta",
  fontSize: 70,
  marginTop: "0px",
  marginBottom: "70px",
  textAlign: "center",
});

export const P = styled.p({
  fontSize: 40,
  marginLeft: "7%",
  marginRight: "7%",
  marginTop: "0px",
  marginBottom: "100px",
  textAlign: "center",
  fontWeight: "bold",
});

export const Footer = styled.footer({
  background: "midnightblue",
  color: "white",
  width: "100%",
  position: "fixed",
  bottom: "0",
});

export const A = styled.a({
  color: "white",
  textDecoration: "none",
});

export const Ul = styled.ul({
  marginLeft: "5%",
});
