import styled from "@emotion/styled";

//types
export const H1 = styled.h1({
  color: "purple",
  fontSize: 60,
  marginLeft: "7%",
  marginTop: "200px",
  marginBottom: "0%",
  textAlign: "justify",
});

export const P = styled.p({
  marginLeft: "7%",
  marginRight: "30%",
  marginTop: "0px",
  textAlign: "justify",
  fontWeight: "bold",
});

export const Footer = styled.footer({
  background: "Yellow",
  width: "100%",
  position: "fixed",
});

export const socialmediaList = styled.li({
  bottom: "0",
  marginLeft: "5%",
});
