import styled from "@emotion/styled";

//types

export const Website = styled.div({
  textAlign: "center",
  backgroundImage: "url('../../src/background.png')",
  backgroundColor: "skyblue",
  width: "100%",
  height: "100%",
});

export const H1 = styled.h1({
  color: "purple",
  fontSize: 100,
  marginTop: "100px",
  textAlign: "center",
});

export const SubHeadings = styled.h1({
  color: "darkgreen",
  fontSize: 70,
  marginTop: "0px",
  marginBottom: "30px",
  textAlign: "center",
});

export const P = styled.p({
  fontSize: 40,
  color: "black",
  marginLeft: "120px",
  marginRight: "120px",
  marginTop: "0px",
  marginBottom: "50px",
  textAlign: "center",
  fontWeight: "bold",
});

export const Footer = styled.footer({
  background: "midnightblue",
  width: "100%",
  position: "fixed",
  bottom: "0",
});

export const Projects = styled.table.th.td({
  textAlign: "center",
  backgroundImage: "url('../../src/background.png')",
  backgroundColor: "skyblue",
  width: "100%",
  height: "100%",
});

export const A = styled.a({
  color: "olive",
  textDecoration: "none",
});

export const Span = styled.span({
  color: "white",
  fontSize: 70,
  backgroundColor: "transparent",
});

//START HELPERS
export const breakpoints: number[] = [576, 768, 992, 1200];

export const mediaQueries: string[] = breakpoints.map(
  (bp) => `@media (max-width: ${bp}px)`
);
//END HELPERS
